# converts to a time in seconds
def toTime(time):
	if '*' in time:
		time = re.split('\*', time)[0].strip()
	if time[0]=="X" or time[0]=="x":
		time = time[1:]
	if re.match(".*:.*", time) == None:
		return float(time)
	return float(re.split(":", time)[0]) * 60 + float(re.split(":", time)[1])

